const input = items[0].json.output;

const [subjectLine, ...rest] = input.split('\n');

const subject = subjectLine.replace(/^Subject:\s*/, '').trim();

let body = rest.join('\n').trim()
  .replace(/\n{2,}/g, '</p><p>')
  .replace(/\n/g, '<br>');

body = `<p>${body}</p>`;

return [
  {
    json: {
      Subject: subject,
      Body: body,
    },
  },
];
